/*↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓ Your code goes here ↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓*/
struct Node{
    int value;
    Node *next = nullptr;
};

class MyList {
    public:
    MyList(int *arr, int total){
        this->total = total;
        head = new Node;
        Node *ptr = head;
        ptr->value = arr[0];
        //cout << ptr->value << "  ";
        for(int i = 1; i<total; i++){
            ptr->next = new Node;
            ptr->value = arr[i];
            //cout << ptr->value << "  ";
        }
    }
    MyList(Node *ptr){
        head = ptr;
    }
    
    MyList *splitList(){
        Node *l1 = head;
        Node *l2 = head->next;
        for(int i = 1; i<=total; i = i+2){
            l1->next = l2->next;
            l1 = l1->next;
            l2->next = l1->next;
            l2 = l2->next;
        }
        MyList *newList = new MyList(l2);
        return newList;
    }
    
    int findDiff(){
        int small = head->value;
        int large = head->value;
        Node *ptr = head;
        while(ptr){
            if(ptr->value>large){
                large = ptr->value;
            }
            if(ptr->value<small){
                small = ptr->value;
            }
            ptr = ptr->next;
        }
        return (large-small);
    }
    
    private:
    int total;
    Node *head  = nullptr;
}; 


